import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  MapPin, 
  Package, 
  Camera, 
  Calculator,
  ArrowLeft
} from 'lucide-react';
import Layout from '../../components/Layout/Layout';
import api from '../../utils/api';
import toast from 'react-hot-toast';
import LoadingSpinner from '../../components/LoadingSpinner';

const CreateOrder = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    pickup_location: '',
    pickup_lat: '',
    pickup_lng: '',
    drop_location: '',
    drop_lat: '',
    drop_lng: '',
    material_type: '',
    material_description: '',
    material_weight: '',
    distance_km: '',
    fare_total: ''
  });
  const [materialPhoto, setMaterialPhoto] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fareCalculated, setFareCalculated] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) { // 2MB limit
        toast.error('Image size should be less than 2MB');
        return;
      }
      
      setMaterialPhoto(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const calculateFare = () => {
    const { pickup_lat, pickup_lng, drop_lat, drop_lng, material_weight } = formData;
    
    if (!pickup_lat || !pickup_lng || !drop_lat || !drop_lng || !material_weight) {
      toast.error('Please fill in all location coordinates and weight');
      return;
    }

    // Calculate distance using Haversine formula
    const R = 6371; // Earth's radius in km
    const dLat = (parseFloat(drop_lat) - parseFloat(pickup_lat)) * Math.PI / 180;
    const dLng = (parseFloat(drop_lng) - parseFloat(pickup_lng)) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(parseFloat(pickup_lat) * Math.PI / 180) * Math.cos(parseFloat(drop_lat) * Math.PI / 180) * 
      Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c;

    // Calculate fare: Base ₹30 + ₹10/km + ₹5/kg
    const baseFare = 30;
    const distanceFare = distance * 10;
    const weightFare = parseFloat(material_weight) * 5;
    const totalFare = baseFare + distanceFare + weightFare;

    setFormData({
      ...formData,
      distance_km: distance.toFixed(2),
      fare_total: totalFare.toFixed(2)
    });
    
    setFareCalculated(true);
    toast.success('Fare calculated successfully!');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!fareCalculated) {
      toast.error('Please calculate fare before submitting');
      return;
    }

    setLoading(true);

    try {
      const submitData = new FormData();
      
      // Add form data
      Object.keys(formData).forEach(key => {
        submitData.append(key, formData[key]);
      });
      
      // Add photo if selected
      if (materialPhoto) {
        submitData.append('material_photo', materialPhoto);
      }

      const response = await api.post('/customer/orders/simple', submitData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      toast.success('Order created successfully!');
      navigate('/customer', { 
        state: { 
          message: `Order ${response.data.order_id} created successfully!` 
        }
      });
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create order');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-8"
        >
          <button
            onClick={() => navigate('/customer')}
            className="inline-flex items-center text-neutral-600 hover:text-primary-600 transition-colors duration-200 mb-4"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Dashboard
          </button>
          <h1 className="text-3xl font-bold text-neutral-900 mb-2">
            Create New Order
          </h1>
          <p className="text-neutral-600">
            Fill in the details to send your package
          </p>
        </motion.div>

        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Pickup & Drop Locations */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            className="card p-6"
          >
            <h2 className="text-xl font-semibold text-neutral-900 mb-4 flex items-center">
              <MapPin className="h-5 w-5 mr-2 text-primary-500" />
              Pickup & Drop Locations
            </h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Pickup Location
                </label>
                <input
                  type="text"
                  name="pickup_location"
                  value={formData.pickup_location}
                  onChange={handleChange}
                  className="input"
                  placeholder="Enter pickup address"
                  required
                />
                <div className="grid grid-cols-2 gap-2 mt-2">
                  <input
                    type="number"
                    name="pickup_lat"
                    value={formData.pickup_lat}
                    onChange={handleChange}
                    className="input"
                    placeholder="Latitude"
                    step="any"
                    required
                  />
                  <input
                    type="number"
                    name="pickup_lng"
                    value={formData.pickup_lng}
                    onChange={handleChange}
                    className="input"
                    placeholder="Longitude"
                    step="any"
                    required
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Drop Location
                </label>
                <input
                  type="text"
                  name="drop_location"
                  value={formData.drop_location}
                  onChange={handleChange}
                  className="input"
                  placeholder="Enter drop address"
                  required
                />
                <div className="grid grid-cols-2 gap-2 mt-2">
                  <input
                    type="number"
                    name="drop_lat"
                    value={formData.drop_lat}
                    onChange={handleChange}
                    className="input"
                    placeholder="Latitude"
                    step="any"
                    required
                  />
                  <input
                    type="number"
                    name="drop_lng"
                    value={formData.drop_lng}
                    onChange={handleChange}
                    className="input"
                    placeholder="Longitude"
                    step="any"
                    required
                  />
                </div>
              </div>
            </div>
          </motion.div>

          {/* Package Details */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="card p-6"
          >
            <h2 className="text-xl font-semibold text-neutral-900 mb-4 flex items-center">
              <Package className="h-5 w-5 mr-2 text-secondary-500" />
              Package Details
            </h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Material Type
                </label>
                <select
                  name="material_type"
                  value={formData.material_type}
                  onChange={handleChange}
                  className="input"
                  required
                >
                  <option value="">Select material type</option>
                  <option value="Documents">Documents</option>
                  <option value="Electronics">Electronics</option>
                  <option value="Clothing">Clothing</option>
                  <option value="Food">Food</option>
                  <option value="Furniture">Furniture</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Weight (kg)
                </label>
                <input
                  type="number"
                  name="material_weight"
                  value={formData.material_weight}
                  onChange={handleChange}
                  className="input"
                  placeholder="Enter weight in kg"
                  min="0.1"
                  step="0.1"
                  required
                />
              </div>
            </div>

            <div className="mt-4">
              <label className="block text-sm font-medium text-neutral-700 mb-2">
                Description (Optional)
              </label>
              <textarea
                name="material_description"
                value={formData.material_description}
                onChange={handleChange}
                className="input"
                rows="3"
                placeholder="Describe your package..."
              />
            </div>

            {/* Photo Upload */}
            <div className="mt-4">
              <label className="block text-sm font-medium text-neutral-700 mb-2">
                Package Photo (Optional)
              </label>
              <div className="flex items-center space-x-4">
                <label className="btn btn-outline cursor-pointer">
                  <Camera className="h-4 w-4 mr-2" />
                  Choose Photo
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handlePhotoChange}
                    className="hidden"
                  />
                </label>
                {photoPreview && (
                  <div className="w-16 h-16 rounded-lg overflow-hidden border border-neutral-200">
                    <img
                      src={photoPreview}
                      alt="Preview"
                      className="w-full h-full object-cover"
                    />
                  </div>
                )}
              </div>
            </div>
          </motion.div>

          {/* Fare Calculation */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
            className="card p-6"
          >
            <h2 className="text-xl font-semibold text-neutral-900 mb-4 flex items-center">
              <Calculator className="h-5 w-5 mr-2 text-accent-500" />
              Fare Calculation
            </h2>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Distance (km)
                </label>
                <input
                  type="number"
                  name="distance_km"
                  value={formData.distance_km}
                  className="input"
                  placeholder="Auto-calculated"
                  readOnly
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-neutral-700 mb-2">
                  Total Fare (₹)
                </label>
                <input
                  type="number"
                  name="fare_total"
                  value={formData.fare_total}
                  className="input"
                  placeholder="Auto-calculated"
                  readOnly
                />
              </div>
              <div className="flex items-end">
                <button
                  type="button"
                  onClick={calculateFare}
                  className="btn btn-secondary w-full"
                >
                  Calculate Fare
                </button>
              </div>
            </div>

            {fareCalculated && (
              <div className="p-4 bg-success-50 border border-success-200 rounded-lg">
                <p className="text-success-800 text-sm">
                  <strong>Fare Breakdown:</strong> Base ₹30 + Distance (₹{(parseFloat(formData.distance_km) * 10).toFixed(2)}) + Weight (₹{(parseFloat(formData.material_weight) * 5).toFixed(2)}) = ₹{formData.fare_total}
                </p>
              </div>
            )}
          </motion.div>

          {/* Submit Button */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4 }}
            className="flex justify-end space-x-4"
          >
            <button
              type="button"
              onClick={() => navigate('/customer')}
              className="btn btn-ghost"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading || !fareCalculated}
              className="btn btn-primary"
            >
              {loading ? (
                <div className="flex items-center">
                  <LoadingSpinner size="sm" className="mr-2" />
                  Creating Order...
                </div>
              ) : (
                'Create Order'
              )}
            </button>
          </motion.div>
        </form>
      </div>
    </Layout>
  );
};

export default CreateOrder;
